import React from "react";
import styled from "styled-components";
const Nav = styled.nav`
    display: flex;
    flex-direction: row;
    /* width: 600px; */
    flex-wrap: wrap;
    justify-content: center;
    color: #bc5a5a;
    list-style: none;
    font-size: 1.4rem;
    margin: 20px 0;
    * {
        padding: 1px 2vw;
    }
    li {
        &:hover {
            background-color: #bc5a5a;
            color: white;
        }
    }
`;
const Menu = () => {
    return (
        <Nav>
            <li>
                <b>Home</b>
            </li>
            <li>Contact</li>
            <li>Language</li>
            <li>Options</li>
        </Nav>
    );
};

export default Menu;
