import React, { useEffect } from "react";
import styled from "styled-components";
import SVG from "../static/svg/svg";
import { SvgContainer } from "./Useless";
const Container = styled.div`
    width: 100vw;
    height: 50px;
    display: flex;

    align-items: center;
    justify-content: center;
    svg {
        width: 35px;
        height: 35px;
        fill: white;
        padding: 2px;
    }
    p {
        padding: 2px;
    }
`;
const ActionMenu = ({ city }) => {
    let existing = "";
    useEffect(() => {
        existing = localStorage.getItem("likedCities");
    }, []);

    const pushLocale = () => {
        console.log(existing);
        existing = existing ? existing.split(",") : [];
        for (let item of existing) {
            if (item == city) {
                const index = existing.indexOf(city);
                if (index > -1) {
                    existing.splice(index, 1);
                }

                localStorage.setItem("likedCities", existing.toString());
                return;
            }
        }
        existing.push(city);

        localStorage.setItem("likedCities", existing.toString());
        // console.log(city);
    };
    return (
        <Container>
            <SVG type="heart" onClick={pushLocale} />
            <p>Like it </p>
        </Container>
    );
};

export default ActionMenu;
