import React from "react";
import styled from "styled-components";
import SVG from "../static/svg/svg";
import { SvgContainer } from "./Useless";
const Container = styled.div`
    width: 100vw;
    height: 50px;
    display: flex;

    align-items: center;
    justify-content: center;
    svg {
        width: 35px;
        height: 35px;
        fill: white;
        padding: 2px;
    }
    p {
        padding: 2px;
    }
`;
const ActionMenu = ({ city }) => {
    const pushLocale = () => {
        var existing = localStorage.getItem("likedCities");

        existing = existing.split(",");
        console.log(existing);
        existing.push(city);
        console.log(existing);

        // localStorage.setItem("likedCities", city);
        // console.log(city);
    };
    return (
        <Container>
            <SVG type="heart" onClick={pushLocale} />
            <p>Like it </p>
        </Container>
    );
};

export default ActionMenu;
