import React, { useState } from "react";
import fetch from "isomorphic-unfetch";
const index = ({ data }) => {
    console.log(data);
    return (
        <div>
            <label>
                <p>Insert City</p>
                <input></input>
            </label>

            <div> {data.name}</div>
        </div>
    );
};

export async function getServerSideProps(ctx) {
    const url =
        "https://geocode.search.hereapi.com/v1/geocode?q=Invalidenstr+117%2C+Rogów&apiKey=FI0ZPYdRRTTdhdTKQwENNV-OAS4OA13qj97BqtkoU2M";

    const fetchedCoords = await (await fetch(url)).json();
    const coords = { lat: fetchedCoords.items[0].position.lat, lng: fetchedCoords.items[0].position.lng };
    console.log(fetchedCoords.items[0].position);
    const url2 = `https://api.openweathermap.org/data/2.5/weather?lat=${coords.lat}&lon=${coords.lng}&appid=ba590e34cd2c5e9d9bfa284e3f0b6a6e`;
    const data = await (await fetch(url2)).json();
    return { props: { data } };
}

export default index;
