import React, { useState } from "react";
import fetch from "isomorphic-unfetch";
const index = ({ data }) => {
    return (
        <div>
            <label>
                <p>Insert City</p>
                <input></input>
            </label>

            <div> {data.name}</div>
            <div> {data.main.temp - 273.15}</div>
        </div>
    );
};

export async function getServerSideProps(ctx) {
    const url =
        "https://geocoder.ls.hereapi.com/6.2/geocode.json?apiKey=FI0ZPYdRRTTdhdTKQwENNV-OAS4OA13qj97BqtkoU2M&searchtext=Paris";

    const fetchedCoords = await (await fetch(url)).json();
    const { Latitude, Longitude } = fetchedCoords.Response.View[0].Result[0].Location.DisplayPosition;
    console.log(fetchedCoords.Response.View[0].Result[0].Location.Address.City);

    ////////////////////////////////////

    const url2 = `https://api.openweathermap.org/data/2.5/weather?lat=${Latitude}&lon=${Longitude}&appid=ba590e34cd2c5e9d9bfa284e3f0b6a6e`;
    const data = await (await fetch(url2)).json();
    return { props: { data } };
}

export default index;
