import React from "react";
import styled from "styled-components";

const blockSize = 140;
const margin = 2;
const Container = styled.div`
    width: 100%;
    color: white;
    background-color: white;
`;
const Scroll = styled.section`
    margin: 0 auto;
    max-height: 200px;
    overflow-x: scroll;
    overflow-y: hidden;
    display: grid;
    grid-template-columns: repeat(8, 1fr);
    width: ${8 * blockSize + margin * 16 + 1 * 16}px;
    overflow: hidden;
    @media (max-width: ${8 * blockSize + margin * 16 + 1 * 16}px) {
        width: 100vw;
        overflow-x: scroll;
    }
`;
const Block = styled.div`
    float: left;
    text-align: center;
    width: ${blockSize}px;
    height: ${blockSize}px;
    display: flex;
    ${(props) => {
        if (props.colors) {
            if (props.colors[2])
                return `background: linear-gradient(180deg, ${props.colors[0]} 0%, ${props.colors[1]} 50.52%, ${props.colors[2]} 100%);`;
            else return `background: linear-gradient(180deg, ${props.colors[0]} 0%,  ${props.colors[1]} 100%);`;
        }
    }}
    justify-content: center;
    flex-direction: column;

    margin: ${margin}px;
    border-radius: 5px;
    @media (max-width: 800px) {
        width: 100px;
        height: 100px;
        font-size: 0.7rem;
    }
`;
const FavouriteCities = () => {
    return (
        <Container>
            <Scroll>
                <Block></Block>
            </Scroll>
        </Container>
    );
};

export default FavouriteCities;
