import styled from "styled-components";

const Section = styled.section`
    width: 100vw;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 30px;
    background: linear-gradient(180deg, #df9e3c 0%, #d9621f 100%);
    color: white;
    flex-grow: 1;
`;
const Temp = styled.p`
    font-size: 5rem;
    padding: 20px;
`;
const City = styled.p`
    font-size: 2rem;
`;
const Date = styled.p`
    font-size: 1.5rem;
    color: #e6e2e2;
`;
const Result = ({ city, temperature }) => {
    return (
        <Section>
            <City>{city}</City>
            <Date>Mon, 8:12 am</Date>
            <Temp>{temperature}Â°</Temp>
        </Section>
    );
};

export default Result;
