import styled from "styled-components";

const Section = styled.section`
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 30px;
    background: linear-gradient(180deg, #df9e3c 0%, #d9621f 100%);
    color: white;
`;
const Temp = styled.p`
    font-size: 5rem;
    padding: 20px;
`;
const Result = ({ city, temperature }) => {
    return (
        <Section>
            <h2>{city}</h2>
            <h4>Mon, 8:12 am</h4>
            <Temp>{temperature}Â°</Temp>
        </Section>
    );
};

export default Result;
