import React from "react";
import getItems from "../customF/ConvertDate";
import SVG from "../static/svg/svg";
import styled from "styled-components";
const blockSize = 150;
const margin = 2;
export const Container = styled.div`
    color: white;
    margin: 0 auto;
`;
export const Block = styled.div`
    float: left;
    text-align: center;
    width: ${blockSize}px;
    height: ${blockSize}px;
    display: flex;
    justify-content: center;
    flex-direction: column;

    /* margin: ${margin}px; */
    border-radius: 5px;
    @media (max-width: 800px) {
        width: 100px;
        height: 100px;
        font-size: 0.7rem;
    }
`;
export const SvgContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    font-size: 1.2rem;
    * {
        margin: 2px 5px;
    }
    svg {
        height: 75px;
        width: 75px;
        fill: white;
    }
    @media (max-width: 600px) {
        font-size: 1.1rem;

        svg {
            height: 30px;
            width: 30px;
            fill: white;
        }
    }
`;
const Useless = ({ wind, sunset, sunrise }) => {
    const { hour: sunriseHour, min: sunriseMinute } = getItems(sunrise);
    const { hour: sunsetHour, min: sunsetMinute } = getItems(sunset);

    const sunriseTime = `${HourConverter(sunriseHour)}:${HourConverter(sunriseMinute)}`;
    const sunsetTime = `${HourConverter(sunsetHour)}:${HourConverter(sunsetMinute)}`;
    return (
        <Container>
            <Block>
                <SvgContainer>
                    Wind strenght
                    <SVG type="windIcon" />
                    {Math.round(wind * 3.6)} km/h
                </SvgContainer>
            </Block>
            <Block>
                <SvgContainer>
                    Sunrise
                    <SVG type="sunrise" />
                    {sunriseTime} am
                </SvgContainer>
            </Block>
            <Block>
                <SvgContainer>
                    Sunset
                    <SVG type="sunset" />
                    {sunsetTime} am
                </SvgContainer>
            </Block>
        </Container>
    );
};
const HourConverter = (item) => {
    if (item < 10) return `0${item}`;
    else return item;
};

export default Useless;
