import React, { useState } from "react";
import fetch from "isomorphic-unfetch";
const index = ({ data }) => {
    return (
        <div>
            <label>
                <p>Insert City</p>
                <input></input>
            </label>

            <div> {data.name}</div>
        </div>
    );
};

export async function getServerSideProps(ctx) {
    const coords = { lat: 49.99395, lon: 18.333241 };

    const url = `https://api.openweathermap.org/data/2.5/weather?lat=${coords.lat}&lon=${coords.lon}&appid=ba590e34cd2c5e9d9bfa284e3f0b6a6e`;
    const res = await fetch(url);
    const data = await res.json();
    console.log(data);
    return { props: { data } };
}

export default index;
