import React, { useEffect, useState } from "react";
import ConvertSkies from "../customF/ConvertSkies";
import styled from "styled-components";
import Link from "next/link";
import axios from "axios";
import SVG from "../static/svg/svg";
const blockSize = 180;
const margin = 10;
const Cont = styled.a`
    float: left;
    text-align: center;
    width: ${blockSize + 40}px;
    height: ${blockSize}px;
    display: flex;
    color: white;
    ${(props) => {
        if (props.colors) {
            if (props.colors[2])
                return `background: linear-gradient(180deg, ${props.colors[0]} 0%, ${props.colors[1]} 50.52%, ${props.colors[2]} 100%);`;
            else return `background: linear-gradient(180deg, ${props.colors[0]} 0%,  ${props.colors[1]} 100%);`;
        }
    }}
    justify-content: center;
    flex-direction: column;
    margin: ${margin}px;
    border-radius: 5px;
    &:focus {
        transform: scale(1.1);
        border-radius: 15px;
        outline: none;
    }
    @media (max-width: 800px) {
        width: 100px;
        height: 100px;
        font-size: 0.7rem;
    }
    &:hover {
        -webkit-filter: brightness(120%);
        -webkit-transition: all 1s ease;
        -moz-transition: all 1s ease;
        -o-transition: all 1s ease;
        -ms-transition: all 1s ease;
        transition: all 1s ease;
    }
`;
const SvgContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    text-align: center;
    justify-content: center;
    * {
        margin: 2px 5px;
    }
    svg {
        height: 40px;
        width: 40px;
        fill: white;
    }
`;
const apikey1 = process.env.API_KEY_1;
const apikey2 = process.env.API_KEY_2;
const initialState = {
    weather: { weather: [{ description: "" }], main: { temp: 0 } },
    city: "",
};
const initialCoords = {
    Response: { View: [] },
};
const Block = ({ city }) => {
    const [data, setData] = useState(initialState);
    const [coords, setCoords] = useState(initialCoords);
    useEffect(() => {
        const fetchData = async () => {
            const url = `https://geocoder.ls.hereapi.com/6.2/geocode.json?apiKey=${apikey1}&searchtext=${city}`;
            const fetchedCoords = await axios(url);
            setCoords(fetchedCoords);
            if (coords.Response.View[0] == undefined) {
                return {
                    props: {
                        error: "oops",
                    },
                };
            }
            const loc = coords.Response.View[0].Result[0].Location;
            const cityName = loc.Address.District ? loc.Address.District : loc.Address.City;
            const { Latitude, Longitude } = loc.DisplayPosition;
            const url2 = `http://api.openweathermap.org/data/2.5/weather?lat=${Latitude}&lon=${Longitude}&appid=${apikey2}`;
            const fetchedData = await axios(url2);
            setData(fetchedData);
        };
        fetchData();
    }, []);

    const temperature = Math.round(data.weather.main.temp - 273.15);
    const { colors, name, icon } = ConvertSkies(data.weather.weather[0].description, temperature);
    const url = `/result?cityName=${data.city}`;
    if (!data) return <div>Loading</div>;
    return (
        <Link href={url}>
            <Cont href="#" colors={colors}>
                <p>{data.city}</p>
                <SvgContainer>
                    <SVG type={icon} />
                    <p>{temperature}</p>
                </SvgContainer>
                <p> {data.weather.weather[0].description}</p>
            </Cont>
        </Link>
    );
};

export default Block;
