import React, { useState } from "react";
import Search from "../components/Search";
import FavouriteCities from "../components/FavouriteCities";
const index = ({ data = [] }) => {
    return (
        <div>
            <Search />
            <FavouriteCities data={data} />
            <FavouriteCities data={data} />
        </div>
    );
};
export async function getServerSideProps(ctx) {
    const favouriteCities = ["rogow", "moskwa", "New York", "las palmas", "paryz", " ankara", "gorzyce"];
    const popularCities = ["rogow", "moskwa", "New York", "las palmas", "paryz", " ankara", "gorzyce"];

    const liked = await f(favouriteCities);
    const popular = await f(popularCities);
    console.log(liked);
    return { props: {} };
}
const f = async (array) => {
    const apikey1 = process.env.API_KEY_1;
    const apikey2 = process.env.API_KEY_2;
    for (let city of (array = [])) {
        const url = `https://geocoder.ls.hereapi.com/6.2/geocode.json?apiKey=${apikey1}&searchtext=${city}`;
        const fetchedCoords = await (await fetch(url)).json();
        if (fetchedCoords.Response.View[0] == undefined) {
            return {
                props: {
                    error: "oops",
                },
            };
        }
        const loc = fetchedCoords.Response.View[0].Result[0].Location;
        const cityName = loc.Address.District ? loc.Address.District : loc.Address.City;
        const { Latitude, Longitude } = loc.DisplayPosition;
        const url2 = `http://api.openweathermap.org/data/2.5/weather?lat=${Latitude}&lon=${Longitude}&appid=${apikey2}
    `;

        const weather = await (await fetch(url2)).json();

        array.push({ weather: weather, city: cityName });
    }
};
export default index;
