import React from "react";
import styled from "styled-components";
import dynamic from "next/dynamic";

import ConvertSkies from "../customF/ConvertSkies";
import Svg from "../static/svg/svg";
const Section = styled.section`
    margin: 0 auto;
    width: 50vw;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 30px;
    ${(props) => {
        if (props.colors[2])
            return `background: linear-gradient(180deg, ${props.colors[0]} 0%, ${props.colors[1]} 50.52%, ${props.colors[2]} 100%);`;
        else return `background: linear-gradient(180deg, ${props.colors[0]} 0%,  ${props.colors[1]} 100%);`;
    }}
    color: white;
    flex-grow: 1;
    svg {
        height: 50px;
        width: 50px;
        fill: white;
    }
`;
const Temp = styled.p`
    font-size: 5rem;
    padding: 20px;
`;
const City = styled.p`
    font-size: 2rem;
`;
const Date = styled.p`
    font-size: 1.5rem;
    color: #e6e2e2;
`;
const Skies = styled.p`
    font-size: 1.5rem;
`;
const SvgContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    flex-wrap: wrap;
    * {
        flex: 1 1 auto;
        margin: 0 10px;
    }
`;
const Result = ({ city, temperature, sky, date }) => {
    const { colors, name, icon } = ConvertSkies(sky, temperature);
    const urls = "../static/svg/rain";

    return (
        <Section colors={colors}>
            <City>{city}</City>
            <Date>{date}</Date>
            <Temp>{temperature}Â°</Temp>
            <SvgContainer>
                <Svg type={icon} />
                <Skies>{sky}</Skies>
            </SvgContainer>
        </Section>
    );
};

export default Result;
