import React from "react";
import styled from "styled-components";
import SVG from "../static/svg/svg";
import ConvertSkies from "../customF/ConvertSkies";
import Link from "next/link";
const blockSize = 180;
const margin = 2;
const Container = styled.div`
    width: 100%;
    color: white;
    background-color: white;
    width: ${8 * blockSize + margin * 16 + 1 * 16}px;
    overflow: hidden;
    margin-top: 30px;
    @media (max-width: ${8 * blockSize + margin * 16 + 1 * 16}px) {
        width: 100vw;
        overflow-x: scroll;
    }
`;
const Scroll = styled.section`
    margin: 0 auto;
    max-height: 200px;
    overflow-x: scroll;
    overflow-y: hidden;
    display: grid;
    grid-template-columns: repeat(8, 1fr);
`;
const Block = styled.div`
    float: left;
    text-align: center;
    width: ${blockSize + 40}px;
    height: ${blockSize}px;
    display: flex;
    ${(props) => {
        if (props.colors) {
            if (props.colors[2])
                return `background: linear-gradient(180deg, ${props.colors[0]} 0%, ${props.colors[1]} 50.52%, ${props.colors[2]} 100%);`;
            else return `background: linear-gradient(180deg, ${props.colors[0]} 0%,  ${props.colors[1]} 100%);`;
        }
    }}
    justify-content: center;
    flex-direction: column;

    margin: ${margin}px;
    border-radius: 5px;
    @media (max-width: 800px) {
        width: 100px;
        height: 100px;
        font-size: 0.7rem;
    }
    &:hover {
        -webkit-filter: brightness(120%);
        -webkit-transition: all 1s ease;
        -moz-transition: all 1s ease;
        -o-transition: all 1s ease;
        -ms-transition: all 1s ease;
        transition: all 1s ease;
    }
`;
const SvgContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    text-align: center;
    justify-content: center;
    * {
        margin: 2px 5px;
    }
    svg {
        height: 40px;
        width: 40px;
        fill: white;
    }
`;
const Header = styled.p`
    font-size: 1.2rem;
    color: black;
`;
const FavouriteCities = ({ data }) => {
    const items = data.map((i) => {
        console.log(i.weather);
        const temperature = Math.round(i.weather.main.temp - 273.15);
        const { colors, name, icon } = ConvertSkies(i.weather.weather[0].description, temperature);
        const url = `/result?cityName=${i.city}`;
        return (
            <Link href={url}>
                <Block colors={colors}>
                    <p>{i.city}</p>
                    <SvgContainer>
                        <SVG type={icon} />
                        <p>{temperature}</p>
                    </SvgContainer>
                    <p> {i.weather.weather[0].description}</p>
                </Block>
            </Link>
        );
    });
    return (
        <Container>
            <Header>Your Liked cities</Header>
            <Scroll> {items}</Scroll>
        </Container>
    );
};

export default FavouriteCities;
