const types = [
    {
        name: ["light rain", "intensity shower rain"],
        icon: "lightRain",
        color: "#C5C5C5",
        color2: "#858383",
    },
    {
        name: ["scattered clouds", "overcast clouds"],
        icon: "cloud",
        color: "#C5C5C5",
        color2: "#858383",
    },
    {
        name: ["clear sky"],
        icon: "clear",
        color: "#3991F3",
        color2: "#66BEFE",
    },
    {
        name: ["few clouds"],
        icon: "cloudWithSun",
        color: "#C5C5C5",
        color2: "#858383",
    },
    {
        name: ["heavy intensity rain", "very heavy rain"],
        icon: "rain",
        color: "#202020",
        color2: "#8D8D8D",
    },
    {
        name: ["fog"],
        icon: "fog",
        colors: ["#202020", null, "#8D8D8D"],
    },
];
export default function ConvertSkies(sky, temp, day) {
    const skyinfo = types.find((item) => {
        const result = item.name.find((name) => {
            if (name === sky) {
                return { ...item, name };
            }
        });
        return result;
    });

    if (!skyinfo) {
        return {
            name: "not found",
            icon: null,
            color: "#C5C5C5",
            color2: "#858383",
        };
    }

    return skyinfo;
}
