import React, { useState } from "react";
import fetch from "isomorphic-unfetch";
import Router from "next/router";
import Search from "../components/Search";
import Result from "../components/Result";

const index = ({ data, city, error }) => {
    if (error) return <div>You passed Wrong localization</div>;
    const temperature = Math.round(data.main.temp - 273.15);
    const sky = data.weather[0].description;
    const [search, setSearch] = useState();
    const handleSubmit = (e) => {
        e.preventDefault();
        e.persist();
        Router.push({
            pathname: `/current`,
            query: { cityName: search },
        });
    };
    const handleChange = (e) => {
        setSearch(e.target.value);
    };

    return (
        <div>
            <Search />
            <Result temperature={temperature} city={city} />

            <div> Skies: {sky} </div>
        </div>
    );
};

export async function getServerSideProps(ctx) {
    const { query } = ctx;

    const queryCity = query.cityName ? query.cityName : "Berlin";

    const url = `https://geocoder.ls.hereapi.com/6.2/geocode.json?apiKey=FI0ZPYdRRTTdhdTKQwENNV-OAS4OA13qj97BqtkoU2M&searchtext=${queryCity}`;

    const fetchedCoords = await (await fetch(url)).json();
    if (fetchedCoords.Response.View[0] == undefined) {
        return {
            props: {
                error: "oops",
            },
        };
    }
    const { Latitude, Longitude } = fetchedCoords.Response.View[0].Result[0].Location.DisplayPosition;
    const city = fetchedCoords.Response.View[0].Result[0].Location.Address.Label;
    // console.log(fetchedCoords.Response.View[0].Result[0].Location.Address);
    // ////////////////////////////////////

    const url2 = `https://api.openweathermap.org/data/2.5/weather?lat=${Latitude}&lon=${Longitude}&appid=ba590e34cd2c5e9d9bfa284e3f0b6a6e`;
    const data = await (await fetch(url2)).json();
    console.log(data);
    return { props: { data, city } };
}

export default index;
