import React from "react";
import styled from "styled-components";

import ConvertSkies from "../customF/ConvertSkies";

import SVG from "../static/svg/svg";
const Container = styled.div`
    display: flex;
    flex-direction: row;
    margin-top: 100px;
    /* max-width: 100vw; */
    overflow: scroll;
`;
const Block = styled.div`
    width: 150px;
    height: 150px;
    border: white solid 1px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;
const Temp = styled.p`
    font-size: 2.5rem;
`;
const SvgContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    flex-wrap: wrap;
    * {
        margin: 0 5px;
    }
    svg {
        height: 40px;
        width: 40px;
        fill: white;
    }
`;

const Forecast = ({ forecast = [] }) => {
    const items = forecast.map((item) => {
        const sky = item.weather[0].description;
        const temperature = Math.round(item.temp.day - 273.15);
        const { colors, name, icon } = ConvertSkies(sky, temperature);
        return (
            <Block>
                <Temp>{temperature}Â°</Temp>
                <SvgContainer>
                    <SVG type={icon} />
                    <p>{sky}</p>
                </SvgContainer>
            </Block>
        );
    });
    return <Container>{items}</Container>;
};

export default Forecast;
