import React, { useEffect, useState } from "react";
import styled from "styled-components";
import SVG from "../static/svg/svg";
import { SvgContainer } from "./Useless";
import { type } from "os";
const Container = styled.div`
    width: 100vw;
    height: 50px;
    display: flex;

    align-items: center;
    justify-content: center;
    svg {
        width: 35px;
        height: 35px;

        padding: 2px;
        path {
            fill: ${(props) => (props.liked ? "white" : "red")};
        }
    }
    p {
        padding: 2px;
    }
`;
const ActionMenu = ({ city }) => {
    const [isLiked, setIsLiked] = useState(false);
    let existing = "";
    useEffect(() => {
        existing = localStorage.getItem("likedCities");
        existing = existing ? existing.split(",") : [];

        let boolean = existing.filter((item) => {
            return item == city;
        });
        if (boolean) setIsLiked(true);
    }, [isLiked]);

    const pushLocale = () => {
        for (let item of existing) {
            if (item == city) {
                const index = existing.indexOf(city);
                if (index > -1) {
                    existing.splice(index, 1);
                }

                localStorage.setItem("likedCities", existing.toString());
                return;
            }
        }
        existing.push(city);

        localStorage.setItem("likedCities", existing.toString());
    };
    return (
        <Container liked={isLiked}>
            <SVG type="heart" onClick={pushLocale} />
            <p>Like it </p>
        </Container>
    );
};

export default ActionMenu;
