import React from "react";
import styled from "styled-components";
import ConvertSkies from "../customF/ConvertSkies";
const Section = styled.section`
    width: 100vw;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 30px;
    ${(props) => `background: linear-gradient(180deg, ${props.colors[0]} 0%, ${props.colors[1]} 100%);`}

    color: white;
    flex-grow: 1;
`;
const Temp = styled.p`
    font-size: 5rem;
    padding: 20px;
`;
const City = styled.p`
    font-size: 2rem;
`;
const Date = styled.p`
    font-size: 1.5rem;
    color: #e6e2e2;
`;
const Skies = styled.p`
    font-size: 1.5rem;
`;
const Result = ({ city, temperature, sky }) => {
    const { color, color2, name, url } = ConvertSkies(sky);
    const SVG = lazy(() => import(url));

    return (
        <Section colors={[color, color2]}>
            <City>{city}</City>
            <Date>Mon, 8:12 am</Date>
            <Temp>{temperature}Â°</Temp>
            <Skies>{sky}</Skies>
        </Section>
    );
};

export default Result;
