import React, { useState } from "react";
import fetch from "isomorphic-unfetch";
import Router from "next/router";
const index = ({ data, city }) => {
    const temperature = Math.round(data.main.temp - 273.15);
    const [search, setSearch] = useState();
    const handleSubmit = (event) => {
        event.persist();
        Router.push({
            pathname: "/",
            query: { id: event.target.value },
        });
    };
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>
                    <p>Insert City</p>
                    <input value={search} name={city}></input>
                </label>
            </form>

            <div> {city}</div>
            <div> {temperature}</div>
        </div>
    );
};

export async function getServerSideProps(ctx) {
    const queryCity = "Gorzyce";
    const url = `https://geocoder.ls.hereapi.com/6.2/geocode.json?apiKey=FI0ZPYdRRTTdhdTKQwENNV-OAS4OA13qj97BqtkoU2M&searchtext=${queryCity}`;

    const fetchedCoords = await (await fetch(url)).json();
    const { Latitude, Longitude } = fetchedCoords.Response.View[0].Result[0].Location.DisplayPosition;
    const city = fetchedCoords.Response.View[0].Result[0].Location.Address.Label;
    console.log(fetchedCoords.Response.View[0].Result[0].Location.Address);
    ////////////////////////////////////

    const url2 = `https://api.openweathermap.org/data/2.5/weather?lat=${Latitude}&lon=${Longitude}&appid=ba590e34cd2c5e9d9bfa284e3f0b6a6e`;
    const data = await (await fetch(url2)).json();
    return { props: { data, city } };
}

export default index;
